import collections
class Solution(object):
    def windowslide(self, source, target):
        targethash = collections.Counter(target)
        headindex, tailindex = 0, 0
        minheadindex, mintailindex = 0, 0
        sourcecapacity, targetcapacity = len(source), len(target)

        while tailindex < sourcecapacity:
            # count how many char was used from target
            if targethash[source[tailindex]] > 0:
                targetcapacity -= 1
            targethash[source[tailindex]] -= 1
            tailindex += 1
            
            # if target be finding out
            if targetcapacity == 0:
                # restore the target hash and move the head index to next until head index equal tailindex
                while headindex < tailindex and targethash[source[headindex]] < 0:
                    targethash[source[headindex]] += 1
                    headindex += 1
                # store the minimum head index and minimum tail index
                if mintailindex == 0:
                    minheadindex, mintailindex = headindex, tailindex
                elif tailindex - headindex <= mintailindex - minheadindex:
                    minheadindex, mintailindex = headindex, tailindex
                    
        return source[minheadindex:mintailindex]
    
    def minWindow(self, s, t):
        """
        :type s: str
        :type t: str
        :rtype: str
        """
        if not s :
            return ''
        elif not t :
            return ''
        
        return self.windowslide(s, t)       
        

if __name__ == "__main__":
    S = ""
    T = ""
    #S = "bbaa"
    #T = "aba"
    idealresult = ""
    result = Solution().minWindow(S,T)
    print(result)
    assert result == idealresult, False